  /**
   * Number of times to repeat tests
   */
  private var repeat: Int = 5

  /**
   * Sizes of the system matrix
   */
  protected var max: Int = 50

  protected var bmax: Int = 10

  /**
   * Numerical tolerance
   */
  protected var rtol: Double = 1e-6

  protected var rmse: Double = 1e-4

  /**
   * Diagonal shift for singularity handling
   */
  protected var shift: Double = 100

  /**
   * Square system matrix
   */
  protected var A: StrideMatrix2D = _

  /**
   * Right hand side, right hand for transpose system, and the solution vector
   * in both cases
   */
  protected var b: StrideMatrix1D = _

  protected var bt: StrideMatrix1D = _

  protected var x: StrideMatrix1D = _

  /**
   * Stores the data of x
   */
  protected var xd: StrideMatrix1D = _

  /**
   * Iterative solver to use
   */
  protected var solver: DoubleIterativeSolver = _

  /**
   * Preconditioner to use
   */
  protected var M: DoublePreconditioner = _

  protected def setUp() {
    createMatrix()
    val n = A.rows()
    x = new DenseMatrix1D(n)
    b = new DenseMatrix1D(n)
    bt = new DenseMatrix1D(n)
    createSolver()
    val im = new DefaultDoubleIterationMonitor(1000000, rtol, 1e-50, 1e+5)
    if (!(solver.isInstanceOf[DoubleHyBR])) {
      solver.setIterationMonitor(im)
    }
    M.setMatrix(A)
    solver.setPreconditioner(M)
    x.assign(1)
    b = A.zMult(x, b)
    bt = A.zMult(x, bt, 1, 0, true)
    xd = x.copy()
    x = new DenseMatrix1D(n)
  }

  protected def createSolver(): Unit

  protected def createMatrix() {
    val Aco = new SparseDoubleMatrix2D(matrix)
    A = Aco.getRowCompressed(true)
  }

  protected def tearDown() {
    A = null
    b = bt = x = null
    xd = null
    solver = null
  }

  def testSolve() {
    try {
      solver.solve(A, b, x)
      assertSolved()
    } catch {
      case e: IterativeSolverDoubleNotConvergedException => fail("Solver did not converge: " + e.getReason + ". Residual=" +
        e.getResidual)
    }
  }

  def testRepeatSolve() {
    try {
      for (i <- 0 until repeat) {
        solver.solve(A, b, x)
        assertSolved()
        x = new DenseMatrix1D(A.rows())
      }
    } catch {
      case e: IterativeSolverDoubleNotConvergedException => fail("Solver did not converge: " + e.getReason + ". Residual=" +
        e.getResidual)
    }
  }

  protected def assertSolved() {
    var numerator = 0
    var denominator = 0
    var tmp: Double = 0.0
    for (i <- 0 until xd.size) {
      tmp = xd.getQuick(i) - x.getQuick(i)
      numerator += (tmp * tmp)
      tmp = xd.getQuick(i)
      denominator += (tmp * tmp)
    }
    assertEquals(0.0, Math.sqrt(numerator) / Math.sqrt(denominator), rmse)
  }
}
