  /**
   * Number of times to repeat tests
   */
  private var repeat: Int = 5

  /**
   * Sizes of the system matrix
   */
  protected var max: Int = 50

  protected var bmax: Int = 10

  /**
   * Numerical tolerance
   */
  protected var rtol: Float = 1e-3f

  protected var rmse: Float = 1e-2f

  /**
   * Diagonal shift for singularity handling
   */
  protected var shift: Float = 100

  /**
   * Square system matrix
   */
  protected var A: FloatMatrix2D = _

  /**
   * Right hand side, right hand for transpose system, and the solution vector
   * in both cases
   */
  protected var b: FloatMatrix1D = _

  protected var bt: FloatMatrix1D = _

  protected var x: FloatMatrix1D = _

  /**
   * Stores the data of x
   */
  protected var xd: FloatMatrix1D = _

  /**
   * Iterative solver to use
   */
  protected var solver: FloatIterativeSolver = _

  /**
   * Preconditioner to use
   */
  protected var M: FloatPreconditioner = _

  protected def setUp() {
    createMatrix()
    val n = A.rows()
    x = new DenseFloatMatrix1D(n)
    b = new DenseFloatMatrix1D(n)
    bt = new DenseFloatMatrix1D(n)
    createSolver()
    val im = new DefaultFloatIterationMonitor(1000000, rtol, 1e-25f, 1e+3f)
    if (!(solver.isInstanceOf[FloatHyBR])) {
      solver.setIterationMonitor(im)
    }
    M.setMatrix(A)
    solver.setPreconditioner(M)
    x.assign(1)
    b = A.zMult(x, b)
    bt = A.zMult(x, bt, 1, 0, true)
    xd = x.copy()
    x = new DenseFloatMatrix1D(n)
  }

  protected def createSolver(): Unit

  protected def createMatrix() {
    val Aco = new SparseFloatMatrix2D(matrix)
    A = Aco.getRowCompressed(true)
  }

  protected def tearDown() {
    A = null
    b = bt = x = null
    xd = null
    solver = null
  }

  def testSolve() {
    try {
      solver.solve(A, b, x)
      assertSolved()
    } catch {
      case e: IterativeSolverFloatNotConvergedException => fail("Solver did not converge: " + e.getReason + ". Residual=" + 
        e.getResidual)
    }
  }

  def testRepeatSolve() {
    try {
      for (i <- 0 until repeat) {
        solver.solve(A, b, x)
        assertSolved()
        x = new DenseFloatMatrix1D(A.rows())
      }
    } catch {
      case e: IterativeSolverFloatNotConvergedException => fail("Solver did not converge: " + e.getReason + ". Residual=" + 
        e.getResidual)
    }
  }

  protected def assertSolved() {
    var numerator = 0
    var denominator = 0
    var tmp: Float = 0.0f
    for (i <- 0 until xd.size) {
      tmp = xd.getQuick(i) - x.getQuick(i)
      numerator += (tmp * tmp)
      tmp = xd.getQuick(i)
      denominator += (tmp * tmp)
    }
    assertEquals(0.0, Math.sqrt(numerator).toFloat / Math.sqrt(denominator).toFloat, rmse)
  }
}
